mapper.select:
select count(*) from t_user u
left join t_contract c on u.uid = c.cid
left join t_renter r on u.uid = r.uid


		and u.uname = #{uname}


		and c.ctele = #{ctele}


		and c.cname = #{cname}


		and r.hid = #{hid}


		and r.rid = #{rid}


		and c.cid = #{cid}

select u.*, c.cid, c.cname, c.ctele, r.rid, r.hid from t_user u
left join t_contract c on u.uid = c.cid
left join t_renter r on u.uid = r.uid


		and u.uname = #{uname}


		and c.utele = #{ctele}


		and c.cname = #{cname}


		and r.hid = #{hid}


		and r.rid = #{rid}


		and c.cid = #{cid}


order by
#{sort}
#{sortOrder}
limit #{offset}, #{ps}

mapper.update:
update t_user
set
uname =
#{uname}, utele = #{utele},
upasswd = #{upasswd}, uemail = #{uemail}
where uid = #{uid}
